'    DBMS_LOB.createtemporary(l_file_clob, TRUE);',
'',
'    DBMS_LOB.converttoclob(',
'        dest_lob     => l_file_clob,',
'        src_blob     => l_file_blob,',
'        amount       => l_file_length,',
'        dest_offset  => l_dest_offset,',
